import stringHash from "string-hash";
import { splitComma } from "@daybrush/utils";

export function getHash(str: string) {
  return stringHash(str).toString(36);
}
export function getShadowRoot(parentElement: HTMLElement | SVGElement) {
    if (parentElement.getRootNode) {
        const rootNode = parentElement.getRootNode();

        if (rootNode.nodeType === 11) {
            return rootNode;
        }
    }
    return;
}
export function injectStyle(className: string, css: string, shadowRoot?: Node) {
  const style = document.createElement("style");

  style.setAttribute("type", "text/css");
  style.innerHTML = css.replace(/([^}{]*){/mg, (all, selector) => {
    return splitComma(selector).map(subSelector => {
      if (subSelector.indexOf(":global") > -1) {
        return subSelector.replace(/\:global/g, "");
      } else if (subSelector.indexOf(":host") > -1) {
        return `${subSelector.replace(/\:host/g, `.${className}`)}`;
      }
      return `.${className} ${subSelector}`;
    }).join(", ") + "{";
  });

  (shadowRoot || document.head || document.body).appendChild(style);
  return style;
}
import { getHash, injectStyle, getShadowRoot } from "./utils";
import { StyledInjector } from "./types";

export default function styled(css: string): StyledInjector {
    const injectClassName = "rCS" + getHash(css);
    let injectCount = 0;
    let injectElement!: HTMLStyleElement;

    return {
        className: injectClassName,
        inject(el: HTMLElement | SVGElement) {
            const shadowRoot = getShadowRoot(el);
            const firstMount = injectCount === 0;
            let styleElement: HTMLStyleElement;

            if (shadowRoot || firstMount) {
                styleElement = injectStyle(injectClassName, css, shadowRoot);
            }
            if (firstMount) {
                injectElement = styleElement;
              }
            if (!shadowRoot) {
                ++injectCount;
            }
            return {
                destroy() {
                    if (shadowRoot) {
                        el.removeChild(styleElement);
                        styleElement = null;
                    } else {
                        if (injectCount > 0) {
                            --injectCount;
                        }
                        if (injectCount === 0 && injectElement) {
                            injectElement.parentNode!.removeChild(injectElement);
                            injectElement = null;
                        }
                    }
                },
            };
        },
    };
}

export * from "./types";
