import React from 'react';
import useForm from '../../form/useForm';
import { DiceIcon } from '../../Icons';
import { Dice, getDiceValues, rollDice } from './DiceUtil';

const Dices = () => {
  const medianCheckboxName = 'useMedian';
  const { formData, handleInputChange } = useForm({ [medianCheckboxName]: true });

  const handleClickDice = (e: React.MouseEvent) => {
    rollDice(parseInt(e.currentTarget.getAttribute('data-sides')!));
  };

  const renderDiceButton = (sides: number) => {
    return (
      <div data-sides={sides} onClick={handleClickDice}>
        <DiceIcon sides={sides} />
      </div>
    );
  };

  return <div className="Dices">{getDiceValues().map(renderDiceButton)}</div>;
};

export default Dices;
